import React from 'react';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';
import PropTypes from 'prop-types';

import { Button } from '../components/Button';
import { Input } from '../components/Input';
import { DropDown } from '../components/DropDown';
import { updateSettings } from '../actions';
import { getCategories } from '../serviceAPI';
import '../styles/settings.css';

class Settings extends React.Component {
  constructor(props) {
    super(props);
    this.initialState = this.initialState.bind(this);
    this.state = {
      categories: [],
      settings: {},
    };
  }

  componentDidMount() {
    this.initialState();
  }

  initialState() {
    const { settings } = this.props;
    this.setState({ settings });
    getCategories().then((categories) => this.setState({ categories }));
  }

  updateSettings(evt) {
    const { name, value } = evt.target;
    this.setState((state) => ({
      ...state,
      settings: { ...state.settings, [`${name}`]: value },
    }));
  }

  renderSelectCategory() {
    const { settings, categories } = this.state;
    const { category } = settings;
    return (
      <label className="label-inputs" htmlFor="inputCategory">
        Category
        <select
          onChange={(evt) => this.updateSettings(evt)}
          name="category"
          value={category}
          className="inputs"
        >
          <option>All</option>
          {categories.map((categoryItem) => (
            <option key={categoryItem.id} value={categoryItem.id}>
              {categoryItem.name}
            </option>
          ))}
        </select>
      </label>
    );
  }

  renderSelectDifficulty() {
    const { settings } = this.state;
    const { difficulty } = settings;
    return (
      <label className="label-inputs" htmlFor="inputDifficulty">
        Difficulty
        <select
          id="inputDifficulty"
          onChange={(evt) => this.updateSettings(evt)}
          name="difficulty"
          value={difficulty}
          className="inputs"
        >
          <option>All</option>
          <option value="easy">Easy</option>
          <option value="medium">Medium</option>
          <option value="hard">Hard</option>
        </select>
      </label>
    );
  }

  renderSelectType() {
    const { settings } = this.state;
    const { type } = settings;
    return (
      <label className="label-inputs" htmlFor="inputType">
        Type
        <select
          id="inputType"
          onChange={(evt) => this.updateSettings(evt)}
          name="type"
          value={type}
          className="inputs"
        >
          <option>All</option>
          <option value="multiple">Multiple Choice</option>
          <option value="boolean">True or False</option>
        </select>
      </label>
    );
  }

  renderBtn() {
    const { saveSettings } = this.props;
    const { settings } = this.state;
    return (
      <Link className="link-btn" to="/">
        <button
          onClick={() => saveSettings(settings)}
          className="btn-addSettings"
          type="button"
        >
          Add Settings
        </button>
      </Link>
    );
  }

  render() {
    const { settings, categories } = this.state;
    const { number, category } = settings;

    return (
      <div className="container-main-play">
        <h1 style={{ color: 'white', fontSize: '3rem', padding: '2rem 0' }}>
          Settings
        </h1>
        <div className="container-settings">
          <label htmlFor="inputNumber">Number Questions</label>
          <Input
            width="30rem"
            textSize="1.6rem"
            type="number"
            name="inputNumber"
            value={number}
            onChange={(evt) => this.updateSettings(evt)}
          ></Input>
          <label className="label-inputs" htmlFor="inputCategory">
            Categories
          </label>
          <DropDown
            items={categories}
            name=""
            value=""
            onChange={(evt) => this.updateSettings(evt)}
          ></DropDown>
          {this.renderSelectCategory()}
          {this.renderSelectDifficulty()}
          {this.renderSelectType()}
        </div>
        <Button title="Save settings"></Button>
        <Button title="Reset default"></Button>
      </div>
    );
  }
}

Settings.propTypes = {
  settings: PropTypes.objectOf(),
  saveSettings: PropTypes.func,
}.isRequired;

const mapStateToProps = (state) => ({
  settings: state.settings,
});

const mapDispatchToProps = (dispatch) => ({
  saveSettings: (value) => dispatch(updateSettings(value)),
});

export default connect(mapStateToProps, mapDispatchToProps)(Settings);
