import React from 'react';

export const DropDown = ({ items, value, name, onChange }) => {
  return (
    <select className="input" value={value} name={name} onChange={onChange}>
      <option>All</option>
      {items.map((item) => (
        <option key={item.id}>{item.name}</option>
      ))}
    </select>
  );
};
