import React from 'react';
import '../styles/ranking.css';

import { Button } from '../components/Button';

class Ranking extends React.Component {
  constructor(props) {
    super(props);
    this.invertRanking = this.invertRanking.bind(this);
    this.setRankingState = this.setRankingState.bind(this);
    this.resetRanking = this.resetRanking.bind(this);
    this.state = {
      ranking: [],
    };
  }

  componentDidMount() {
    this.setRankingState();
  }

  setRankingState() {
    const ranking = JSON.parse(localStorage.getItem('ranking'));
    if (ranking) this.setState({ ranking });
  }

  resetRanking() {
    localStorage.setItem('ranking', JSON.stringify([]));
    this.setRankingState();
  }

  invertRanking() {
    const { ranking } = this.state;
    if (ranking.length >= 2) {
      if (ranking[0].score > ranking[1].score)
        ranking.sort((a, b) => a.score - b.score);
      else ranking.sort((a, b) => b.score - a.score);
      this.setState({ ranking });
    }
  }

  renderBtns() {
    return (
      <div className="container-btns-ranking">
        <Button title="Home"></Button>
        <Button title="Clear" onClick={this.resetRanking}></Button>
        <button
          onClick={this.invertRanking}
          className="btn-setup"
          type="button"
        >
          Invert
        </button>
      </div>
    );
  }

  render() {
    const { ranking } = this.state;

    return (
      <div className="container-main-play">
        <h1 style={{ color: 'white', fontSize: '3rem', padding: '2rem 0' }}>
          Ranking
        </h1>
        {this.renderBtns()}
        {ranking.map((player, index) => (
          <div className="container-ranking" key={index}>
            <span className="span-number">{`${index + 1}#`}</span>
            <img
              className="img-gravatar"
              src={player.gravatarEmail}
              alt="player"
            />
            <div className="container-info">
              <p data-testid={`player-name-${index}`}>{player.name}</p>
              <p data-testid={`player-score-${index}`}>{player.score}</p>
            </div>
          </div>
        ))}
      </div>
    );
  }
}

export default Ranking;
