import { SAVE_ASKS, RESET_GAME } from '../actions';

const INITIAL_STATE = {
  asks: [],
  status: false,
};

const game = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case SAVE_ASKS:
      return [...action.asks];
    case RESET_GAME:
      return INITIAL_STATE;
    default:
      return state;
  }
};

export default game;
