import React from 'react';

export const Button = ({
  icon,
  title,
  textColor = 'black',
  textSize = '1rem',
}) => {
  return (
    <div className="container-button">
      {icon}
      <p style={({ color: textColor }, { fontSize: textSize })}>{title}</p>
    </div>
  );
};
