import React from 'react';

import '../styles/toast.css';
import { TiDelete } from 'react-icons/ti';
import { IoMdWarning } from 'react-icons/io';
import { FaCheckCircle } from 'react-icons/fa';

const TYPES_TOAST = {
  error: {
    icon: <TiDelete color="white" size="2.5rem" />,
    color: '#ca4042',
  },
  warning: {
    icon: <IoMdWarning color="white" size="2.5rem" />,
    color: '#FFFF00',
  },
  sucess: {
    icon: <FaCheckCircle color="white" size="2.5rem" />,
    color: '#49a356',
  },
};

export const Toast = ({ title, type, description = '' }) => {
  return (
    <div
      className="container-toast"
      style={{ backgroundColor: TYPES_TOAST[type].color }}
    >
      {TYPES_TOAST[type].icon}
      <p>{title}</p>
      <p>{description}</p>
    </div>
  );
};
