import React from 'react';

import '../styles/toast.css';
import { TiDelete } from 'react-icons/ti';

const TYPES_TOAST = [
  {
    name: 'error',
    color: '#FF0000',
  },
  { name: 'warning', color: '#FFFF00' },
  { name: 'success', color: '#00FF00' },
];

export const Toast = ({ title, type, description = '' }) => {
  return (
    <div
      className="container-toast"
      style={{ backgroundColor: type === 'red' }}
    >
      <TiDelete color="white" size="2.5rem" />
      <h3>{title}</h3>
      <h4>{description}</h4>
    </div>
  );
};
