import React from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';

import { Button } from '../components/Button';

class Feedback extends React.Component {
  render() {
    const valuePattern = 3;
    const { name, score, avatar, assertions, quantityQuestion } = this.props;

    return (
      <div className="container-feedback">
        <img src={avatar} alt="avatarImage"></img>
        <h1>{name}</h1>
        <p>
          {assertions >= valuePattern ? 'Mandou bem!' : 'Podia ser melhor...'}
        </p>
        <p>
          <span>{'Score '}</span>
          {score}
        </p>
        {assertions === 0 ? (
          <p>Ops, you didn't get any questions right</p>
        ) : (
          <p data-testid="feedback-total-question">
            {`${assertions} questions right`}
          </p>
        )}
        <div>
          <div className="container-btns">
            <Button title="Play Again"></Button>
            <Button title="Ranking"></Button>
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  name: state.player.name,
  avatar: state.player.gravatarEmail,
  score: state.player.score,
  assertions: state.player.assertions,
  quantityQuestion: state.settings.number,
});

Feedback.propTypes = {
  name: PropTypes.string.isRequired,
  avatar: PropTypes.string.isRequired,
  score: PropTypes.number.isRequired,
  assertions: PropTypes.number.isRequired,
  quantityQuestion: PropTypes.number.isRequired,
};

export default connect(mapStateToProps)(Feedback);
