import React from 'react';

import '../styles/toast.css';
import { TiDelete } from 'react-icons/ti';

const TYPES_TOAST = {
  error: {
    icon: <TiDelete color="white" size="2.5rem" />,
    color: '#FF0000',
  },
  warning: { name: 'warning', color: '#FFFF00' },
  sucess: { name: 'success', color: '#00FF00' },
};

export const Toast = ({ title, type, description = '' }) => {
  return (
    <div
      className="container-toast"
      style={{ backgroundColor: TYPES_TOAST[type].color }}
    >
      {TYPES_TOAST[type].icon}
      <p>{title}</p>
      <p>{description}</p>
    </div>
  );
};
