import React from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';
import { asyncToken, loginAction } from '../actions';
import { createRanking } from '../services/localStorage';
import { getGravatar } from '../serviceAPI';
import '../styles/home.css';
import trophyIcon from '../images/trophyIcon.png';
import gearIcon from '../images/gearIcon.png';

import { BsFillTrophyFill } from 'react-icons/bs';
import { IoMdSettings } from 'react-icons/io';
import { Button } from '../components/Button';

class Home extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      username: '',
      email: '',
    };
    this.handleChange = this.handleChange.bind(this);
  }

  componentDidMount() {
    window.scrollTo(0, 0);
  }

  handleChange({ target }) {
    const { name, value } = target;
    this.setState({
      [name]: value,
    });
  }

  handleClick() {
    const { username, email } = this.state;
    const { loginActionFunc, saveToken, settings } = this.props;
    saveToken(settings);
    loginActionFunc(username, email);
    getGravatar(email).then((response) => {
      const player = {
        player: {
          name: username,
          assertions: 0,
          score: 0,
          gravatarEmail: response.url,
        },
      };
      localStorage.setItem('state', JSON.stringify(player));
    });
    createRanking();
  }

  renderSettings() {
    return (
      <Link className="container-link" to="/settings">
        <Button icon={<IoMdSettings></IoMdSettings>} title="Settings"></Button>
      </Link>
    );
  }

  renderForm() {
    const { username, email } = this.state;
    return (
      <form>
        <label className="label-input" htmlFor="player-name">
          Player Name
          <input
            className="input-name"
            id="player-name"
            name="username"
            data-testid="input-player-name"
            onChange={this.handleChange}
            value={username}
          />
        </label>
        <label className="label-input" htmlFor="gravatar-email">
          Gravatar E-mail
          <input
            className="input-email"
            id="gravatar-email"
            name="email"
            data-testid="input-gravatar-email"
            onChange={this.handleChange}
            value={email}
          />
        </label>
        <Link to="/play">
          <button
            className="btn-play"
            type="submit"
            data-testid="btn-play"
            disabled={!email || !username}
            onClick={() => this.handleClick()}
          >
            Play
          </button>
        </Link>
      </form>
    );
  }

  renderRanking() {
    return (
      <Link className="container-link" to="/ranking">
        <BsFillTrophyFill color="white" size="16px"></BsFillTrophyFill>
        <button className="btn-setup" ype="button" data-testid="btn-ranking">
          Ranking
        </button>
      </Link>
    );
  }

  render() {
    return (
      <div className="container-main">
        <div className="container-top">
          {this.renderForm()}
          {this.renderRanking()}
          {this.renderSettings()}
        </div>
      </div>
    );
  }
}

Home.propTypes = {
  saveToken: PropTypes.func.isRequired,
  loginActionFunc: PropTypes.func.isRequired,
  settings: PropTypes.objectOf().isRequired,
};

const mapStateToProps = (state) => ({
  settings: state.settings,
});

const mapDispatchToProps = (dispatch) => ({
  saveToken: (value) => dispatch(asyncToken(value)),
  loginActionFunc: (username, email) => dispatch(loginAction(username, email)),
});

export default connect(mapStateToProps, mapDispatchToProps)(Home);
