import React from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { asyncToken, loginAction } from '../actions';
import { createRanking } from '../services/localStorage';
import { getGravatar } from '../serviceAPI';
import '../styles/home.css';

import { BsFillTrophyFill } from 'react-icons/bs';
import { IoMdSettings } from 'react-icons/io';
import { Button } from '../components/Button';

class Home extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      username: '',
      email: '',
    };
    this.handleChange = this.handleChange.bind(this);
  }

  componentDidMount() {
    window.scrollTo(0, 0);
  }

  goFor = (pageName) => {
    this.props.history.push(`/${pageName}`);
  };

  handleChangeText({ target }) {
    const { name, value } = target;
    this.setState({
      [name]: value,
    });
  }

  handleClickPlay() {
    const { username, email } = this.state;
    const { loginActionFunc, saveToken, settings } = this.props;
    saveToken(settings);
    loginActionFunc(username, email);
    getGravatar(email).then((response) => {
      const player = {
        player: {
          name: username,
          assertions: 0,
          score: 0,
          gravatarEmail: response.url,
        },
      };
      localStorage.setItem('state', JSON.stringify(player));
    });
    createRanking();
  }

  renderForm() {
    const { username, email } = this.state;
    return (
      <form>
        <label className="label-input" htmlFor="player-name">
          Player Name
          <input
            className="input-name"
            id="player-name"
            name="username"
            data-testid="input-player-name"
            onChange={this.handleChangeText}
            value={username}
          />
        </label>
        <label className="label-input" htmlFor="gravatar-email">
          Gravatar E-mail
          <input
            className="input-email"
            id="gravatar-email"
            name="email"
            data-testid="input-gravatar-email"
            onChange={this.handleChangeText}
            value={email}
          />
        </label>
        <button
          type="submit"
          data-testid="btn-play"
          disabled={!email || !username}
          onClick={() => this.handleClickPlay()}
        >
          Play
        </button>
      </form>
    );
  }

  render() {
    return (
      <div className="container-main">
        <div className="container-top">
          {this.renderForm()}
          <Button
            icon={<BsFillTrophyFill color="white" size="1.6rem" />}
            title="Ranking"
            textColor="white"
            textWeight="600"
            textSize="1.6rem"
            onClick={() => this.goFor('ranking')}
          ></Button>
          <Button
            icon={<IoMdSettings color="white" size="1.8rem" />}
            title="Settings"
            textColor="white"
            textWeight="600"
            textSize="1.6rem"
            onClick={() => this.goFor('settings')}
          ></Button>
        </div>
      </div>
    );
  }
}

Home.propTypes = {
  saveToken: PropTypes.func.isRequired,
  loginActionFunc: PropTypes.func.isRequired,
  settings: PropTypes.objectOf().isRequired,
};

const mapStateToProps = (state) => ({
  settings: state.settings,
});

const mapDispatchToProps = (dispatch) => ({
  saveToken: (value) => dispatch(asyncToken(value)),
  loginActionFunc: (username, email) => dispatch(loginAction(username, email)),
});

export default connect(mapStateToProps, mapDispatchToProps)(Home);
