import { LOGIN, UPDATE_PLAYER } from '../actions';

const INITIAL_STATE = {
  name: '',
  email: '',
  assertions: 0,
  score: 0,
  gravatarEmail: '',
};

const player = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case UPDATE_PLAYER:
      return action.player;
    case LOGIN:
      return {
        ...state,
        name: action.username,
        gravatarEmail: action.email,
      };
    default:
      return state;
  }
};

export default player;
