import React from 'react';

export const Input = ({
  type,
  name,
  value,
  onChange,
  placeHolder,
  textColor,
  textSize,
  width = '15rem',
}) => {
  return (
    <input
      style={{ width: width, fontSize: textSize }}
      type={type}
      name={name}
      value={value}
      placeholder={placeHolder}
      onChange={onChange}
    ></input>
  );
};
