import React from 'react';

export const Button = ({
  icon,
  title,
  textColor = 'black',
  textSize = '1rem',
  textWeight = '400',
}) => {
  return (
    <div className="container-button">
      {icon}
      <p
        style={{ color: textColor, fontSize: textSize, fontWeight: textWeight }}
      >
        {title}
      </p>
    </div>
  );
};
